import React, { useState } from 'react';

function Fp3(props) {
    const [post,setpost]=useState("");
    const [q,setq]=useState("");
    const [skills,setskills]=useState("");  
    const submit=((e)=>{let Alert = '';
    e.preventDefault();
    setpost(post.trim());
      setq(q.trim());
      setskills(skills.trim());
      console.log(skills.length);
    if(!(/^((([a-zA-Z]+\s){0,2})([a-zA-Z]+))$/.test(post))){
         Alert ="position is not correct or empty";}
    if(!(q.toLowerCase()==="postgraduate"||q.toLowerCase()==="undergraduate" || q.toLowerCase()==="matric" ||q.toLowerCase()==="intermediate")){
        Alert+="\nQualification is not correct or empty";
    }
   
    if(!/^((\w+\s){0,}(\w+))*$/.test(skills.toLowerCase())){
        Alert+="\nskill is not correct or empty";
    
    }
    if(Alert.length>0)alert(Alert);

    else{ 
      props.addfp({Post:{post},Qualification:{q},Skills:{skills}});
     
    }
  });

  return (
    <div className='min-container'>
<p className='text-center' ><b>{'* Are mendatory fields'}</b></p>
      <form action="" onSubmit={submit} id="fp2" className='container'>
      <label htmlFor='linkedin'>*Linked in profile url</label>
      <input type='text' name='linkedin'  onChange={((e)=>{setpost(e.target.value)})} id='post' placeholder='Enter your linked in url' value={post} className='p-3 w-5'/>
      <label htmlFor='p' >personal web or blog url</label>
      <input type='text'  name='p' id='p' onChange={((e)=>{setq(e.target.value)})} placeholder='If you have your personal website to show your project then enter its url' value={p}  className='p-3 w-5'></input>
      <label htmlFor='say' >Anything to say</label>
      <textarea rows={10} cols={30} name='say' id='say' onChange={((e)=>{setskills(e.target.value)})} placeholder='Do you want to say something?'  value={skills}  className='p-3 w-5'></textarea>
      
      <button className="btn btn-danger btn-sm " >Submit</button>
    </form>
    </div>
  )
}
Fp3.propTypes = {

}

export default Fp3
